mapper.update:
UPDATE component
SET component_cd = #{componentCd}, component_name = #{componentName},
food_flag = #{foodFlag}, component_status = #{componentStatus}
WHERE component_id = #{componentId}

mapper.select:
SELECT *
FROM component
WHERE component_id LIKE CONCAT('%',#{id},'%')
AND food_flag = #{flag}
AND component_status = #{status}

SELECT *
FROM component
WHERE component_cd LIKE CONCAT('%',#{cd},'%')
AND food_flag = #{flag}
AND component_status = #{status}

SELECT *
FROM component
WHERE component_name LIKE CONCAT('%',#{name},'%')
AND food_flag = #{flag}
AND component_status = #{status}

SELECT *
FROM component
WHERE component_cd LIKE CONCAT('%',#{cd},'%')
AND component_name LIKE CONCAT('%',#{name},'%')
AND food_flag = #{flag}
AND component_status = #{status}

SELECT *
FROM component
WHERE component_id LIKE CONCAT('%',#{id},'%')
AND component_name LIKE CONCAT('%',#{name},'%')
AND food_flag = #{flag}
AND component_status = #{status}

SELECT *
FROM component
WHERE component_id LIKE CONCAT('%',#{id},'%')
AND component_cd LIKE CONCAT('%',#{cd},'%')
AND food_flag = #{flag}
AND component_status = #{status}
